import React, { useState } from "react";

const FoodsForm = ({onCreate, Foods}) => {

    const [stateIngredient, setStateIngredient] = useState ({
        name: "",
        calories: 0,
        footType: null,
        pantryFood: "",
        fridgeFood: ""
    })

    const handleChange = (event) => {
        let propertyName = event.target.name;
        let copyIngredient = {...stateIngredient}
        copyIngredient[propertyName] = event.target.value;
        setStateIngredient(copyIngredient);
    }

    const handleSubmit = (event) => {
        event.preventDefault();
        onCreate(stateIngredient);
    }

    return(
        <div>
            Hello
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Name" name="name" onChange={handleChange} value={stateRecipe.name}/>
                <input type="number" placeholder="Cook Time" name="cookTime" onChange={handleChange} value={parseInt(stateRecipe.cookTime)}/>
                <input type="text" placeholder="Method" name="method" onChange={handleChange} value={stateRecipe.method}/>
                <input type="text" placeholder="Method" name="method" onChange={handleChange} value={stateRecipe.method}/>
                <button type="submit">Save</button>
            </form>
            <p>Hello</p>
        </div>
    )
}

export default FoodsForm;
