    //         <div key={index}>

    //             <h4><i>{recipe.name}</i></h4>
    //         </div>
    //     )
    // })

    const allRecipes = () => {
        if(parseInt(recipes.id) === parseInt(commonRecipes)){
            return recipes.name
        }
    }


  return (
    <div>SelectedFood
        {showSelected}
        <p>{allRecipes}</p>
        {commonRecipes}
    </div>

  )
})


export default SelectedFood
